  componentDidMount() {
    this.getBackbone();
  }

  async getBackbone() {
    let url = 'https://secrethost.azurewebsites.net/visual/backbone'
    const response = await fetch(url);
    let json = await response.json();
    this.setState({ Backbone: json, IsLoading: false });
  }

  static renderVisual(backbone) {
    return (
      <div className={backbone.Style}>
        {  VisualElement.renderVisualElement(backbone)}
      </div>
    );
  }
  static renderAllVisual(backbone) {
    let arr = []
    for (let key in backbone) {
      console.log(backbone[key])
      arr.push(
        <div className={backbone[key].Style}>
          <div>
            {VisualBuilder.renderVisual(backbone[key])}
          </div>
        </div>
      )
    }
    return arr
  }
  render() {
    let direction = this.state.Direction
    let contents = this.state.IsLoading
      ? <p><em>Loading...</em></p>
      : VisualBuilder.renderVisual(this.state.Mock[direction]);
    return (
      <div>
        {contents}
      </div>

    );
  }


